mapper.update:


UPDATE insurance

SET
      update_time = NOW()
    , ins_type = #{ins_type}
    , ins_co = #{ins_co}
    , contract_date = #{contract_date}
    , contract_no = #{contract_no}
    , contractor = #{contractor}
    , insured = #{insured}
    , prod_name = #{prod_name}
    , payt_exp = #{payt_exp}
    , premium = #{premium}
    , renew_prod_yn = IFNULL(#{renew_prod_yn}, 'N')
    , digi_sig_yn = IFNULL(#{digi_sig_yn}, 'N')
    , memo_col = #{memo_col}
 WHERE
    idx = #{idx}
